import {useParams} from "react-router";
import {useEffect, useState} from "react";
import MenuServices from "../../../../services/menuServices/MenuServices";
import Toast from "../../../../components/toast-message/ToastMessage";
import MaterialService from "../../../../services/warehouseServices/MaterialService";

export default function MaterialDetail() {
    const {id} = useParams();
    const [materialDetail, setMaterialDetail] = useState({});

    const getMaterialDetail = async (id) => {
        try {
            const response = await MaterialService.checkDetail(id);
            if (response.success) {
                setMaterialDetail(response.data);
            } else {
                Toast.error(response.message);
            }
        } catch (error) {
            Toast.error(error);
        }
    }

    useEffect(() => {
        getMaterialDetail(id);
    }, [id])

    return (
        <div>
            <div> Image (thÃªm sau)</div>
            <div>Name: {materialDetail.name}</div>
            <div>Id: {materialDetail.id}</div>
            <div>Code: {materialDetail.code}</div>
            <div>Shelf Life: {materialDetail.shelfLife} days</div>
            <div>Unit type: {materialDetail.unitType}</div>
            <div>Description: {materialDetail.description}</div>
            <div>Created At: {materialDetail.createdAt}</div>
            <div>Updated At: {materialDetail.updatedAt}</div>
        </div>
    )
}
