import React, { useState } from 'react';
import { Navigate, useLocation, useNavigate } from 'react-router-dom';
import styles from './Cart.module.scss'; 
import { check } from 'prettier';

function Cart() {
	
    const location = useLocation();
    const navigate = useNavigate();
    const [cartItems, setCartItems] = useState(location.state?.cartItems || []);

    const handleQuantityChange = (id, newQuantity) => {
        setCartItems(cartItems.map(item =>
            item.id === id ? { ...item, quantity: newQuantity } : item
        ));
    };

    const handleRemove = (id) => {
        setCartItems(cartItems.filter(item => item.id !== id));
    };

    const handleBuy = () => {
        if (cartItems.length > 0) {
            
            alert(`you want buy ${cartItems.length} products.`);
            console.log("Proceeding to checkout with items: ", cartItems);
        
        } else {
            
            alert("Your cart is empty. Please add items to your cart before proceeding to checkout.");
        }
        
    };

    const goHome = () => {
        navigate('/');
    };

    const calculateTotalPrice = () => {
        return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    return (
        <div style={{ backgroundImage: 'linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(/images/background.jpg)' }}>
            <div className={styles.cart} >
                <button onClick={goHome} className={styles.homeButton}>Home</button>
                <h1><i class="fas fa-shopping-cart"></i>Your Cart</h1>

                <div className={styles.cartItemsWrapper}>
                    {cartItems.length > 0 ? (
                        cartItems.map(item => (
                            <div key={item.id} className={styles.cartItem}>
                                <img src={item.image} alt={item.name} className={styles.cartItemImage} />
                                <div className={styles.cartItemDetails}>
                                    <h2>{item.name}</h2>
                                    <p>Price: ${item.price}</p>
                                    <p>
                                        Quantity:
                                        <input
                                            type="number"
                                            value={item.quantity}
                                            min="1"
                                            onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
                                            className={styles.quantityInput}
                                        />
                                    </p>
                                    <button
                                        onClick={() => handleRemove(item.id)}
                                        className={styles.removeButton}
                                    >
                                        Remove
                                    </button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p></p>
                    )}
                </div>
                
                {cartItems.length > 0 && (
                    <div className={styles.buttons}>
                        <p className={styles.totalPrice}>Total Price: ${calculateTotalPrice().toFixed(2)}</p>
                        <button onClick={handleBuy} className={styles.buyButton}>
                            Buy
                        </button>
                    </div>
                )}
            </div>

            <footer class={styles.footer}>
                <div class={styles.footerContent}>
                    <div class={styles.footerLeft}>
                        <h3>@e-project 2</h3>
                        <p>&copy; 2024 Food Store. All Rights Reserved.</p>
                    </div>
                    <div class={styles.footerCenter}>
                        <h4>Links</h4>
                        <ul>
                            <li><a href="#"><i class="fas fa-home"></i> Home</a></li>
                            <li><a href="#"><i class="fas fa-info-circle"></i> About Us</a></li>
                            <li><a href="#"><i class="fas fa-shopping-cart"></i> Cart</a></li>
                            <li><a href="#"><i class="fas fa-user"></i> Login</a></li>
                            <li><a href="#"><i class="fas fa-blog"></i> Blog</a></li>
                        </ul>
                    </div>
                    <div class={styles.footerRight}>
                        <h4>Follow Us</h4>
                        <div class={styles.socialIcons}>
                            <a href="#"><i class="fab fa-facebook-f"></i></a>
                            <a href="#"><i class="fab fa-twitter"></i></a>
                            <a href="#"><i class="fab fa-instagram"></i></a>
                            <a href="#"><i class="fab fa-linkedin-in"></i></a>
                        </div>
                    </div>
                    <div class={styles.footerAddress}>
                        <h4>Address</h4>
                        <a href="https://www.google.com/maps/place/Detech+Building/@21.028765,105.781789,14z/data=!4m6!3m5!1s0x313454b3285df81f:0x97be82a66bbe646b!8m2!3d21.0287649!4d105.7817893!16s%2Fg%2F11b7sy4x6v?hl=vi&entry=ttu" target="_blank" class={styles.addressLink}>
                            <i class="fa-solid fa-location-dot"></i> 8 Tôn Thất Thuyết, Mỹ Đình, Nam Từ Liêm, Hà Nội, Việt Nam
                        </a>
                        <iframe 
                            class={styles.mapIframe} 
                            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d7448.164880305965!2d105.7816318452492!3d21.029387120259692!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x313454b3285df81f%3A0x97be82a66bbe646b!2sDetech%20Building!5e0!3m2!1svi!2s!4v1713486964354!5m2!1svi!2s"
                            width="100%" 
                            height="250" 
                            allowFullScreen="" 
                            loading="lazy" 
                            referrerPolicy="no-referrer-when-downgrade">
                        </iframe>
                    </div>
                </div>
            </footer>
         </div>
    );
}

export default Cart;
