import React, { useState, useEffect, useMemo, useCallback } from 'react';
import styles from './Cart.module.scss';
import OrderServices from '../../../services/menuServices/OrderServices';
import Toast from '../../../components/toast-message/ToastMessage';
import kitchenServices from '../../../services/orderServices/OrderServices.js';
import { useOutletContext } from 'react-router-dom';

function Cart() {
  const [orders, setOrders] = useState([]);
  const { updateCartCount } = useOutletContext(); 

  
  useEffect(() => {
    const storedOrders = JSON.parse(localStorage.getItem('order')) || [];
    setOrders(storedOrders);
  }, []);

  
  const handleRemoveItem = useCallback((id) => {
    const updatedOrders = orders.filter(order => order.id !== id);
    setOrders(updatedOrders);
    localStorage.setItem('order', JSON.stringify(updatedOrders));
    updateCartCount(); 
  }, [orders]);

 
  const handleIncreaseQuantity = useCallback((id) => {
    const updatedOrders = orders.map(order => 
      order.id === id ? { ...order, quantity: (order.quantity || 1) + 1 } : order
    );
    setOrders(updatedOrders);
    localStorage.setItem('order', JSON.stringify(updatedOrders));
  }, [orders]);

 
  const handleDecreaseQuantity = useCallback((id) => {
    const updatedOrders = orders.map(order => {
      if (order.id === id) {
        const newQuantity = (order.quantity || 1) - 1;
        return newQuantity > 0 ? { ...order, quantity: newQuantity } : null;
      }
      return order;
      
    }).filter(order => order !== null); // Remove items with quantity <= 0

    setOrders(updatedOrders);
    localStorage.setItem('order', JSON.stringify(updatedOrders));
   
  }, [orders],updateCartCount() );

  // Calculate the total price of orders
  const totalPrice = useMemo(() => {
    return orders.reduce((acc, order) => acc + (order.price * (order.quantity || 1)), 0);
  }, [orders]);

  // Handle checkout process
  // const handleCheckout = useCallback(() => {
  //   if (orders.length === 0) { 
  //     alert('You do not have any products yet.');
  //   } else {
  //     alert('Purchase successful');
  //     setOrders([]);  
  //     localStorage.removeItem('order');  
  //   }
  // }, [orders]);

  const handleBuy = function(){
    var order = JSON.parse(localStorage.getItem('order'));
          if (!order || order.length === 0) {
            Toast.error("Giỏ hàng trống!");
            return;
        }
        // console.log(order);s
        order.map((order) => {
          var product = {
            productId: order.productId,
            name : order.name,
            price: order.price,
            quantity: order.quantity,
            cooktime: order.cookTime,
            status: 'new',
        };
        
      console.log(product);

        OrderServices.createOrder(product)
        .then(response => {
              
              console.log("Đơn hàng đã được tạo:", response);
              
          })
          .catch(error => {
            
              console.error("Lỗi khi tạo đơn hàng:", error);
          });
        })  
        localStorage.removeItem('order'); 
        setOrders([]);
        updateCartCount(); 
        Toast.success("Đơn hàng đã được tạo")
        console.log(order)
  }

  return (
    
    <div className="container my-4">
      <h2 className="text-center mb-4">Shopping Cart</h2>
      <div className="row">
        <div className="col-md-8">
          {orders.length === 0 ? (
            <div className="alert alert-warning text-center" role="alert">
              Your Cart !
            </div>
          ) : (
            <table className="table table-bordered">
              <thead className="thead-light">
                <tr>
                  <th>Image</th>
                  <th>Name Product</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th>cooking time</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(order => (
                  <tr key={order.id}>
                    <td>
                      <img 
                        src={`/images/${order.name}.jpg`}
                        alt={order.name}
                        style={{ width: '100px', height: 'auto' }}
                      />
                    </td>
                    <td>{order.name}</td>
                    <td>{order.price} vnđ</td>
                    <td>
                      <button 
                        className="btn btn-secondary btn-sm" 
                        onClick={() => handleDecreaseQuantity(order.id)} 
                        disabled={(order.quantity || 1) <= 1}
                      >
                        -
                      </button>
                      <span className="mx-2">{(order.quantity || 1)}</span>
                      <button 
                        className="btn btn-secondary btn-sm" 
                        onClick={() => handleIncreaseQuantity(order.id)}
                      >
                        +
                      </button>
                    </td>
                    <td>{order.cookTime} minute</td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleRemoveItem(order.id)}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Cart Summary</h5>
              <p>Total Items: {orders.length}</p>
              <p>Total Price: {totalPrice.toFixed(3)} vnđ</p>
              <button className="btn btn-success btn-block" onClick={handleBuy}>
                Buy
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cart;
