import React, { useState, useMemo, useEffect } from 'react';
import styles from './Home.module.scss';
import menuServices from '../../../services/menuServices/MenuServices';
import Toast from '../../../components/toast-message/ToastMessage';
import { useOutletContext } from 'react-router-dom';

function Home() {
    const [currentPage, setCurrentPage] = useState(1);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [priceRange, setPriceRange] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const { updateCartCount } = useOutletContext();

    const images = [
        { id: 1, image: '/images/pizza.jpg' },
        { id: 2, image: '/images/burger.jpg' },
        { id: 3, image: '/images/sushi.jpg' },
        { id: 4, image: '/images/cake.jpg' },
        { id: 5, image: '/images/kfc.jpg' },
        { id: 6, image: '/images/bread.jpg' },
        { id: 7, image: '/images/rice.jpg' },
        { id: 8, image: '/images/donut.jpg' },
        { id: 9, image: '/images/banhbao.jpg' },
        { id: 10, image: '/images/trungchien.jpg' },
        { id: 11, image: '/images/thitkhotau.jpg' },
    ];

    const imagebody = [
        '/images/pizza.jpg',
        '/images/kfc.jpg',
        '/images/burger.jpg',
        '/images/banhbao.jpg'
    ];

    const itemsPerPage = 7;

    const [menus, setMenus] = useState([]);
    const getMenu = async () => {
        console.log(menus);
        try {
            const response = await menuServices.getList();
            if (response.success) {
                Toast.success(response.message);
                setMenus(response.data);
            } else {
                Toast.error(response.message);
            }
        } catch (error) {
            Toast.error(error);
        }
    };

    useEffect(() => {
        getMenu();
    }, []);

    // ảnh 3 giây đổi
    useEffect(() => {
        const intervalId = setInterval(() => {
            setCurrentImageIndex(prevIndex => (prevIndex + 1) % imagebody.length);
        }, 3000);

        return () => clearInterval(intervalId); 
    }, []);

    const totalPages = Math.ceil(menus.length / itemsPerPage);

    //lọc sản phẩm
    const filteredMenus = useMemo(() => {
        return menus.filter(menu => {
            const categoryMatch = selectedCategory ? menu.category === selectedCategory : true;
            const priceMatch = priceRange ? (menu.price >= priceRange[0] && menu.price <= priceRange[1]) : true;
            const searchMatch = menu.name.toLowerCase().includes(searchQuery.toLowerCase()); // Search query match

            return categoryMatch && priceMatch && searchMatch;
        });
    }, [menus, selectedCategory, priceRange, searchQuery]);

    // phân trang
    const paginatedMenus = useMemo(() => {
        const startIndex = (currentPage - 1) * itemsPerPage;
        const endIndex = startIndex + itemsPerPage;
        return filteredMenus.slice(startIndex, endIndex);
    }, [filteredMenus, currentPage]);

    const goToNextPage = () => {
        setCurrentPage(prev => Math.min(prev + 1, totalPages));
    };

    const goToPreviousPage = () => {
        setCurrentPage(prev => Math.max(prev - 1, 1));
    };


    //xử lý Oder
    const handleOrder = (menu) => {
        let order = JSON.parse(localStorage.getItem('order')) || [];
        const existingItem = order.find(item => item.id === menu.id);
    
        if (!existingItem) {
            // Thêm sản phẩm mới nếu không tồn tại
            order.push({ ...menu, quantity: 1 });
            Toast.success(`${menu.name} added to cart.`);
            updateCartCount(); // Chỉ tăng số lượng trong giỏ hàng khi có sản phẩm mới
        } else {
            // Nếu sản phẩm đã tồn tại, không tăng số lượng mà chỉ hiển thị thông báo
            Toast.info(`${menu.name} is already in the cart.`);
        }
    
        // Cập nhật lại localStorage với giỏ hàng hiện tại
        localStorage.setItem('order', JSON.stringify(order));
        console.log(JSON.stringify(order));
    };


    // danh mục 
    const categories = [...new Set(menus.map(menu => menu.category))];

    // chọn giá 
    const priceRanges = [
        { label: 'Dưới 50k', value: [0, 50000] },
        { label: '50k - 100k', value: [50000, 100000] },
        { label: 'Trên 100k', value: [100000, Infinity] },
    ];

    return (
        <>
            {/* Main Content Section */}
            <main className={styles.mainContent}>
                <div className={styles.leftSection}>
                    <h2>Healthy & Quality</h2>
                    <h1>Delicious Food Menu</h1>
                    
                </div>
                <div className={styles.rightSection}>
                    <img
                        src={`${process.env.PUBLIC_URL}${imagebody[currentImageIndex]}`}
                        alt="Food"
                        className={styles.foodImage}
                    />
                </div>
                <div className={styles.offerCircle}>
                    <p>Up to<br />Save<br />50% off !</p>
                </div>
            </main>



            <div className={styles.menuContent}>Our Menu</div>

                        {/*  Filter */}
                        
                        <div className={styles.filterContainer}>
                            <div className={styles.searchBox}>
                                <h3>Search</h3>
                                <input
                                    type="text"
                                    placeholder="Search menu..."
                                    value={searchQuery}  
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                />
                            </div>
                            <div className={styles.categorySelect}>
                                <h3>Category</h3>
                                <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory}>
                                    <option value="">Tất cả</option>
                                    {categories.map(category => (
                                        <option key={category} value={category}>{category}</option>
                                    ))}
                                </select>
                            </div>
                            <div className={styles.priceRange}>
                                <h3>Price Range</h3>
                                {priceRanges.map(range => (
                                    <div key={range.label} className="form-check">
                                        <input 
                                            type="radio" 
                                            name="priceRange" 
                                            id={range.label} 
                                            value={range.label} 
                                            onChange={() => setPriceRange(range.value)} 
                                        />
                                        <label htmlFor={range.label}>
                                            {range.label}
                                        </label>
                                    </div>
                                ))}
                            </div>
                        </div>
           
            {/* Product Cards Section */}
            <div className={styles.productCards}>
                {paginatedMenus.map((menu, index) => (
                    <div key={menu.id} className={styles.card}>
                        {menu.avatar == null || menu.avatar == undefined? 
                         <img   src={images[index < images.length ? index : 1].image}  />
                         :
                         <img src={menu.avatar} />
                    }
                        <h3>{menu.name}</h3>
                        <span>Category: <strong>{menu.category}</strong></span>
                        <p className={styles.price}>Price {menu.price.toFixed(3)} VNĐ</p>
                        <button className={styles.orderBtn} onClick={() => handleOrder(menu)}>Order Now</button>
                    </div>
                ))}
            </div>

            {/* Pagination */}
            <div className={styles.pagination}>
                <button onClick={goToPreviousPage} disabled={currentPage === 1}>Previous</button>
                <span>Page {currentPage} of {totalPages}</span>
                <button onClick={goToNextPage} disabled={currentPage === totalPages}>Next</button>
            </div>
        </>
    );
}

export default Home;
