import { useEffect, useState } from 'react';
import InventoryService from '../../../../services/warehouseServices/InventoryService';
import Toast from '../../../../components/toast-message/ToastMessage';
import CustomTable from '../../../../components/custom-table/CustomTable';
import ImExRecipeService from '../../../../services/accountantService/ImExRecipeService';
import CustomModalForm from '../../../../components/custom-modal/CustomModalForm';
import * as Yup from 'yup';
import CustomModalDetail from "../../../../components/custom-modal/CustomModalDetail";

export default function ImExRecipe({ repType }) {
	const [imExRecipeList, setImExRecipeList] = useState([]);
	const [detail, setDetail] = useState(null);
	const [pagination, setPagination] = useState({
		page: 0,
		size: 10,
	});

	const getRecipeList = async (page) => {
		try {
			const response = await ImExRecipeService.getList(page, repType);
			if (response.success) {
				setImExRecipeList(response.data);
			} else {
				Toast.error(response.message);
			}
		} catch (error) {
			Toast.error(error);
		}
	};

	const paginate = (page) => {
		setPagination({
			page: page,
			size: 10,
		});
	};

	useEffect(() => {
		getRecipeList(pagination);
	}, [pagination, repType]);

	const createNewRecipe = async (data) => {
		console.log(data);

		// try {
		// 	const response = await ImExRecipeService.createNew(data);
		// 	if (response.success) {
		// 		Toast.success(response.message);
		// 	} else {
		// 		Toast.error(response.message);
		// 	}
		// } catch (error) {
		// 	Toast.error(error);
		// }
	};
	const getDetail = async (id) => {
		try {
			const response = await ImExRecipeService.getDetail(id);
			if (response.success) {
				setDetail(response.data);
			} else {
				Toast.error(response.message);
			}
		} catch (error) {
			Toast.error(error);
		}
	}

	return (
		<>
			<CustomModalForm
				title={'Import recipe'}
				properties={[
					{
						field: 'Responsible By',
						type: 'text',
						validation: Yup.number().required('Responsible By is required'),
						className: 'col-md-6',
					},
					{
						field: 'Rep Type',
						type: 'text',
						validation: Yup.string().required('Report Type is required'),
						className: 'col-md-6',
					},
					{
						field: 'Purpose',
						type: 'number',
						validation: Yup.string().required('Purpose is required'),
						className: 'col-md-6',
					},
				]}
				modalID="importRecipeModal"
				submitFunction={(values) => createNewRecipe(values)}
			/>
			<div className="card-header">
				<button
					className="btn btn-primary"
					data-bs-toggle="modal"
					data-bs-target="#importRecipeModal"
				>
					Create new recipe
				</button>
			</div>
			<CustomModalDetail detailData={detail}></CustomModalDetail>
			<div className="card-body">
				{imExRecipeList && (
					<CustomTable data={imExRecipeList} endpoint={'cms/accountant/recipe'} actions={{
					detail: getDetail}
					} />
				)}
			</div>
		</>
	);
}
