import MaterialService from "../../../../services/warehouseServices/MaterialService";
import Toast from "../../../../components/toast-message/ToastMessage";
import Table from '../../../../components/custom-table/CustomTable'
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Material() {
	const [dataMaterials, setDataMaterials] = useState([]);
	const [pagination, setPagination] = useState({
		page: 0,
		size: 10
	});
	const navigate = useNavigate();

	const getMaterials = async (phantrang) => {
		try {
			const response = await MaterialService.getMaterials(phantrang);
			if (response.success) {
				setDataMaterials(response.data)
			} else {
				Toast.error(response.message);
			}
		} catch (error) {
			Toast.error(error);
		}
	};

	// const addMaterials = async function (formID) {
	// 	const materialData = GetFormValues(formID)
	// 	console.log("a");

	// 	console.log(materialData);

	// 	try {
	// 		const response = await MaterialService.addMaterials(materialData);
	// 		if (response.success) {
	// 			Toast.success("Added material success.");

	// 		} else {
	// 			Toast.error("Failed to add material.");
	// 		}
	// 	} catch (error) {
	// 		Toast.error(error);
	// 	}
	// };

	const phantrang = (page) => {
		setPagination({
			page: page,
			size: 10
		})
	}

	useEffect(() => {
		getMaterials(pagination)
	}, [pagination])

	const openCreateForm = () => {
		navigate("/cms/warehouse/materials/create")
	}

	return (
		<div>
			<div className="card-header">
				<button className="btn btn-primary" onClick={() => openCreateForm()}>Create new material</button>
			</div> 
			{/* todo need remove of change latter */}

			{/* <button className="btn btn-primary me-2" onClick={() => getMaterials(pagination)}>TEST</button> */}
			<button className="btn btn-success me-2" onClick={() => phantrang(1)}>trang 2</button>
			<button className="btn btn-success " onClick={() => phantrang(0)}>trang 1 </button>
			{dataMaterials && (<Table data={dataMaterials} />)}
		</div>
	);
}

export default Material;
