import React, { useState, useMemo,useEffect } from 'react';
import styles from './Home.module.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faShoppingCart, faEdit, faBlog, faPhone, faUser } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

function Home() {
	
    const [filter, setFilter] = useState({
        category: '',
        priceRange: '',
        rating: '',
    });

    const [searchQuery, setSearchQuery] = useState('');
    const [cart, setCart] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 10;

    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    const products = [
        { id: 1, name: 'Pizza', category: 'pizza', price: 12.99, quantity: 40, image: '/images/pizza.jpg' },
        { id: 2, name: 'Burger', category: 'burger', price: 8.99, quantity: 50, image: '/images/burger.jpg' },
        { id: 3, name: 'Sushi', category: 'sushi', price: 15.99, quantity: 30, image: '/images/sushi.jpg' },
        { id: 4, name: 'Chocolate Cake', category: 'dessert', price: 6.99, quantity: 40, image: '/images/cake.jpg' },
        { id: 5, name: 'Chicken KFC', category: 'kfc', price: 5.23, quantity: 40, image: '/images/kfc.jpg' },
        { id: 6, name: 'Pate Bread', category: 'bread', price: 4.99, quantity: 50, image: '/images/bread.jpg' },
        { id: 7, name: 'Cơm sườn', category: 'rice', price: 15.99, quantity: 30, image: '/images/rice.jpg' },
        { id: 8, name: 'Donut', category: 'dessert', price: 6.99, quantity: 40, image: '/images/donut.jpg' },
        { id: 9, name: 'Dumplings', category: 'dessert', price: 3.99, quantity: 40, image: '/images/banhbao.jpg' },
        { id: 10, name: 'Trứng chiên', category: 'rice', price: 4.99, quantity: 40, image: '/images/trungchien.jpg' },
        { id: 11, name: 'Thịt kho tàu', category: 'rice', price: 2.99, quantity: 40, image: '/images/thitkhotau.jpg' },
        
    ];

    const filteredProducts = useMemo(() => {
        let result = products;

        if (filter.category) {
            result = result.filter(product => product.category === filter.category);
        }

        if (filter.priceRange) {
            result = result.filter(product => {
                if (filter.priceRange === 'low') return product.price < 10;
                if (filter.priceRange === 'medium') return product.price >= 10 && product.price <= 20;
                if (filter.priceRange === 'high') return product.price > 20;
                return true;
            });
        }

        if (filter.rating) {
            result = result.filter(product => product.rating === parseInt(filter.rating));
        }

        if (searchQuery) {
            result = result.filter(product =>
                product.name.toLowerCase().includes(searchQuery.toLowerCase())
            );
        }

        return result;
    }, [filter, searchQuery]);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilter({
            ...filter,
            [name]: value,
        });
    };

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const addToCart = (product) => {
        const existingProduct = cart.find(item => item.id === product.id);
        if (existingProduct) {
            setCart(cart.map(item =>
                item.id === product.id
                    ? { ...item, quantity: item.quantity + 1 }
                    : item
            ));
        } else {
            setCart([...cart, { ...product, quantity: 1 }]);
        }
    };

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = filteredProducts.slice(indexOfFirstItem, indexOfLastItem);

    const totalPages = Math.ceil(filteredProducts.length / itemsPerPage);

    const goToNextPage = () => {
        setCurrentPage(prev => Math.min(prev + 1, totalPages));
    };

    const goToPreviousPage = () => {
        setCurrentPage(prev => Math.max(prev - 1, 1));
    };

    const images = [
        '/images/pizza.jpg',
        '/images/kfc.jpg',
        '/images/burger.jpg',
        '/images/banhbao.jpg'
    ];

    useEffect(() => {
        const intervalId = setInterval(() => {
            setCurrentImageIndex(prevIndex => (prevIndex + 1) % images.length);
        }, 3000);

        return () => clearInterval(intervalId);
    }, [images.length]);

    return (
        <div className={styles.container}>
            {/* Header Section */}
            <header className={styles.header}>
            <Link to="/" className={styles.logoLink}>
                        <img src="/logo.png" alt="Logo" className={styles.logo} />
                    </Link>
                <nav className={styles.navMenu}>
                <ul>
            <li>
                <Link to="/">
                    <FontAwesomeIcon icon={faHome} /> Home
                </Link>
            </li>
            <li>
                <Link to="cart" state={{  cartItems: cart  }}>
                    <FontAwesomeIcon icon={faShoppingCart} /> Cart
                </Link>
            </li>
            <li>
                <Link to="authen">
                    <FontAwesomeIcon icon={faUser} /> Login
                </Link>
            </li>
            {/* <li>
                <Link to="cms">
                    <FontAwesomeIcon icon={faEdit} /> CMS
                </Link>
            </li> */}
            <li>
                <Link to="#">
                    <FontAwesomeIcon icon={faBlog} /> Blog
                </Link>
            </li>
            <li>
                <Link to="#">
                    <FontAwesomeIcon icon={faPhone} /> Contact Us
                </Link>
            </li>
        </ul>
                </nav>
                <div className={styles.headerRight}>
                    <input
                        type="text"
                        className={styles.searchBar}
                        placeholder="Enter food..."
                        value={searchQuery}
                        onChange={handleSearchChange}
                    />
                    <div className={styles.contactInfo}>
                        <p class="fas fa-phone">+0298.999.999</p>
                        <p>Contact us to order</p>
                    </div>
                </div>
            </header>

            {/* Main Content Section */}
            <main className={styles.mainContent}>
                <div className={styles.leftSection}>
                    <h2>Healthy & Quality</h2>
                    <h1>Delicious Food Menu</h1>
                    <button className={styles.orderNowBtn}>Order Now</button>
                </div>
                <div className={styles.rightSection}>
                    <img
                        src={images[currentImageIndex]}
                        alt="Food"
                        className={styles.foodImage}
                    />
                </div>
                <div className={styles.offerCircle}>
                    <p>Up to<br />Save<br />50% off !</p>
                </div>
            </main>
            <div className={styles.menuContent}>Menu</div>
            {/* Product Cards Section */}
            <div className={styles.productCards}>
                {currentItems.map(product => (
                    <div key={product.id} className={styles.card}>
                        <img src={product.image} alt={product.name} />
                        <h3>{product.name}</h3>
                        <p>limited quantity: <strong>{product.quantity}</strong></p>
                        <p className={styles.price}>Only ${product.price}</p>
                        <button className={styles.orderBtn} onClick={() => addToCart(product)}>Order Now</button>
                    </div>
                ))}
            </div>
            
            {/* Pagination */}
            <div className={styles.pagination}>
                <button onClick={goToPreviousPage} disabled={currentPage === 1}>Previous</button>
                <span>Page {currentPage} of {totalPages}</span>
                <button onClick={goToNextPage} disabled={currentPage === totalPages}>Next</button>
            </div>

            <footer class={styles.footer}>
    <div class={styles.footerContent}>
        <div class={styles.footerLeft}>
            <h3>@e-project 2</h3>
            <p>&copy; 2024 Food Store. All Rights Reserved.</p>
        </div>
        <div class={styles.footerCenter}>
            <h4>Links</h4>
            <ul>
                <li><a href="#"><i class="fas fa-home"></i> Home</a></li>
                <li><a href="#"><i class="fas fa-info-circle"></i> About Us</a></li>
                <li><a href="#"><i class="fas fa-shopping-cart"></i> Cart</a></li>
                <li><a href="#"><i class="fas fa-user"></i> Login</a></li>
                <li><a href="#"><i class="fas fa-blog"></i> Blog</a></li>
            </ul>
        </div>
        <div class={styles.footerRight}>
            <h4>Follow Us</h4>
            <div class={styles.socialIcons}>
                <a href="#"><i class="fab fa-facebook-f"></i></a>
                <a href="#"><i class="fab fa-twitter"></i></a>
                <a href="#"><i class="fab fa-instagram"></i></a>
                <a href="#"><i class="fab fa-linkedin-in"></i></a>
            </div>
        </div>
        <div class={styles.footerAddress}>
            <h4>Address</h4>
            <a href="https://www.google.com/maps/place/Detech+Building/@21.028765,105.781789,14z/data=!4m6!3m5!1s0x313454b3285df81f:0x97be82a66bbe646b!8m2!3d21.0287649!4d105.7817893!16s%2Fg%2F11b7sy4x6v?hl=vi&entry=ttu" target="_blank" class={styles.addressLink}>
                <i class="fa-solid fa-location-dot"></i> 8 Tôn Thất Thuyết, Mỹ Đình, Nam Từ Liêm, Hà Nội, Việt Nam
            </a>
            <iframe 
                class={styles.mapIframe} 
                src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d7448.164880305965!2d105.7816318452492!3d21.029387120259692!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x313454b3285df81f%3A0x97be82a66bbe646b!2sDetech%20Building!5e0!3m2!1svi!2s!4v1713486964354!5m2!1svi!2s"
                width="100%" 
                height="250" 
                allowFullScreen="" 
                loading="lazy" 
                referrerPolicy="no-referrer-when-downgrade">
            </iframe>
        </div>
    </div>
</footer>

        </div>
    );
}

export default Home;
