import React, { useState } from 'react';
import authenServices from '../../services/userServices/AuthenServices';
import Toast from '../../components/toast-message/ToastMessage';
// import style from './Register.module.scss';

function Register() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        confirmPassword: '',
        fullName: '',
        primaryPhone: '',
        email: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleRegister = async (e) => {
        e.preventDefault();

        if (formData.password !== formData.confirmPassword) {
            Toast.error('Passwords do not match!');
            return;
        }

        try {
            const response = await authenServices.register({
                username: formData.username,
                password: formData.password,
                fullName: formData.fullName,
                primaryPhone: formData.primaryPhone,
                email: formData.email,
            });

            if (response.success) {
                Toast.success('Registration successful!');
                setFormData({
                    username: '',
                    password: '',
                    confirmPassword: '',
                    fullName: '',
                    primaryPhone: '',
                    email: '',
                });
            } else {
                Toast.error(response.message || 'Registration failed.');
            }
        } catch (error) {
            Toast.error('An error occurred during registration.');
        }
    };

    return (
        <div className={style.registerContainer}>
            <h3 className="mb-4 text-center fs-2 fw-bolder">Register</h3>
            <form onSubmit={handleRegister} className={style.registerForm}>
                <div className={style.formGroup}>
                    <input
                        type="text"
                        name="username"
                        placeholder="Username"
                        value={formData.username}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className={style.formGroup}>
                    <input
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={formData.password}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className={style.formGroup}>
                    <input
                        type="password"
                        name="confirmPassword"
                        placeholder="Confirm Password"
                        value={formData.confirmPassword}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className={style.formGroup}>
                    <input
                        type="text"
                        name="fullName"
                        placeholder="Full Name"
                        value={formData.fullName}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className={style.formGroup}>
                    <input
                        type="text"
                        name="primaryPhone"
                        placeholder="Phone Number"
                        value={formData.primaryPhone}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className={style.formGroup}>
                    <input
                        type="email"
                        name="email"
                        placeholder="Email Address"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div>
                    <button type="submit" className={style.registerButton}>
                        Register
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Register;
