import { Navigate } from 'react-router';
import { useCookies } from 'react-cookie';
import {jwtDecode} from 'jwt-decode';

const UserProfile = ({ roles, children }) => {
  const [cookies] = useCookies(['token']);
  const token = cookies.token;

  if (!token) {
    return <Navigate to="/cms/login" replace />;
  }

  try {
    const decodedToken = jwtDecode(token);

    // Kiểm tra nếu role không nằm trong danh sách được phép
    if (roles && !roles.includes(decodedToken.role)) {
      return <Navigate to="/" replace />;
    }

    return children;
  } catch (error) {
    // Trường hợp lỗi giải mã token hoặc token không hợp lệ
    
  }

	return (
		<div className="user-profile">
			<h2>Welcome, {user?.username}</h2>
			<p>Role: {user?.role}</p>
			<button onClick={handleLogout}>Logout</button>
			<Navigate to="/cms/login" replace />;
		</div>

	);
}

export default UserProfile;
