import app from '../../api/baseApi';

const getOrderList = async function () {
	try {
		const response = await app.get('/order-dish');
		return response.data;
	} catch (error) {
		throw error;
	}
};

const createOrder = async function (data) {
	try {
		const response = await app.post('/order-dish', data); // Truyền trực tiếp `data`
		return response.data;
	} catch (error) {
		throw error;
	}
};

const updateOrder = async function (id, data) {
	try {
		const response = await app.put(`/order-dish/${id}`, data);
		return response.data;
	} catch (error) {
		throw error;
	}
};

const cancelOrder = async function (id) {
	try {
		const response = await app.delete(`/order-dish/${id}`); // Không truyền `data`
		return response.data;
	} catch (error) {
		throw error;
	}
};

const respondToOrder = async function (id, data) {
	try {
		const response = await app.post(`/order-dish/${id}/response`, data);
		return response.data;
	} catch (error) {
		throw error;
	}
};

const confirmPayment = async function (id, data) {
	try {
		const response = await app.put(`/orders/payment/${id}`, data);
		return response.data;
	} catch (error) {
		throw error;
	}
};

const shipOrder = async function (id, data) {
	try {
		const response = await app.put(`/orders/shipping/${id}`, data);
		return response.data;
	} catch (error) {
		throw error;
	}
};

const orderServices = {
	getOrderList,
	createOrder,
	updateOrder,
	cancelOrder,
	respondToOrder,
	confirmPayment,
	shipOrder,
	// changeOrderStatus,
};

export default orderServices;
