import React, { useState, useEffect, useMemo, useCallback } from 'react';
import styles from './Cart.module.scss';
import Toast from '../../../components/toast-message/ToastMessage';
import kitchenServices from '../../../services/orderServices/OrderService';
import { useOutletContext } from 'react-router-dom';
import * as Yup from 'yup';
import { Formik, Form, Field } from 'formik';
import ServiceMethods from "../../../utils/ServiceMethods";
import menuServices from "../../../services/menuServices/MenuServices";

function Cart() {
  const [orders, setOrders] = useState([]);
  const [menuDishes, setMenuDishes] = useState([]);
  const { updateCartCount } = useOutletContext();
  const pagination = { page: 0, size: 10 }; // Cấu hình phân trang

  // Lấy dữ liệu từ API khi component được mount
  useEffect(() => {
    const storedOrders = JSON.parse(localStorage.getItem('order')) || [];
    setOrders(storedOrders);

    // Lấy menu từ server
    const getMenu = async () => {
      await ServiceMethods.get(menuServices.getList, pagination, (data) => {
        setMenuDishes(data);
      });
    };
    getMenu();
  }, []);

  // Thêm sản phẩm vào giỏ hàng
  const addToCart = useCallback((order, quantity) => {
    setOrders((prevOrders) => {
      const existingOrder = prevOrders.find(item => item.id === order.id);
      if (existingOrder) {
        return prevOrders.map(item =>
          item.id === order.id
            ? { ...item, quantity: item.quantity + quantity }
            : item
        );
      } else {
        return [...prevOrders, { ...order, quantity }];
      }
    });
  }, []);

  const handleRemoveItem = useCallback((id) => {
    const updatedOrders = orders.filter(order => order.id !== id);
    setOrders(updatedOrders);
    localStorage.setItem('order', JSON.stringify(updatedOrders));
    updateCartCount();
  }, [orders]);

  const handleIncreaseQuantity = useCallback((id) => {
    addToCart(orders.find(order => order.id === id), 1);
  }, [orders, addToCart]);

  const handleDecreaseQuantity = useCallback((id) => {
    const order = orders.find(order => order.id === id);
    if (order && order.quantity > 1) {
      addToCart(order, -1);
    } else {
      handleRemoveItem(id);
    }
  }, [orders, addToCart, handleRemoveItem]);

  // Tính tổng giá trị đơn hàng
  const totalPrice = useMemo(() => {
    return orders.reduce((acc, order) => acc + (order.price * (order.quantity || 1)), 0);
  }, [orders]);

  // Gửi đơn hàng
  const submitOrder = async (values) => {
    const detailList = orders.map(order => ({
      menuId: order.id,
      quantity: order.quantity,
    }));

    const orderRequest = {
      description: values.description,
      promotionCode: values.promotionCode,
      status: "WAITING",
      detailList,
    };

    try {
      const response = await kitchenServices.createOrder(orderRequest);
      Toast.success("Đơn hàng đã được tạo");
      setOrders([]);
      localStorage.removeItem('order');
      updateCartCount();
    } catch (error) {
      Toast.error("Lỗi khi tạo đơn hàng");
      console.error("Error creating order", error);
    }
  };

  return (
    <div className="container my-4">
      <h2 className="text-center mb-4">Shopping Cart</h2>
      <div className="row">
        <div className="col-md-8">
          {menuDishes.length > 0 && (
            <div>
              <h2>Menu Dishes</h2>
              <ul>
                {menuDishes.map((dish) => (
                  <li key={dish.id}>
                    <h3>{dish.name}</h3>
                    <input
                      type="number"
                      min="1"
                      defaultValue="1"
                      id={`quantity-${dish.id}`}
                    />
                    <button onClick={() => {
                      const quantity = parseInt(document.getElementById(`quantity-${dish.id}`).value, 10);
                      addToCart(dish, quantity);
                    }}>
                      Add to Cart
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {orders.length === 0 ? (
            <div className="alert alert-warning text-center" role="alert">
              Your Cart is Empty!
            </div>
          ) : (
            <table className="table table-bordered">
              <thead className="thead-light">
                <tr>
                  <th>Image</th>
                  <th>Name Product</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th>Cooking Time</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(order => (
                  <tr key={order.id}>
                    <td>
                      <img
                        src={`/images/${order.name}.jpg`}
                        alt={order.name}
                        style={{ width: '100px', height: 'auto' }}
                      />
                    </td>
                    <td>{order.name}</td>
                    <td>{order.price} vnđ</td>
                    <td>
                      <button
                        className="btn btn-secondary btn-sm"
                        onClick={() => handleDecreaseQuantity(order.id)}
                        disabled={(order.quantity || 1) <= 1}
                      >
                        -
                      </button>
                      <span className="mx-2">{(order.quantity || 1)}</span>
                      <button
                        className="btn btn-secondary btn-sm"
                        onClick={() => handleIncreaseQuantity(order.id)}
                      >
                        +
                      </button>
                    </td>
                    <td>{order.cookTime} minute</td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleRemoveItem(order.id)}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Cart Summary</h5>
              <p>Total Items: {orders.length}</p>
              <p>Total Price: {totalPrice.toFixed(3)} vnđ</p>

              <Formik
                initialValues={{ description: '', promotionCode: '' }}
                validationSchema={Yup.object({
                  description: Yup.string().required('Required'),
                  promotionCode: Yup.string(),
                })}
                onSubmit={submitOrder}
              >
                {({ touched, errors }) => (
                  <Form>
                    <div>
                      <label>Description:</label>
                      <Field name="description" type="text" />
                      {touched.description && errors.description ? (
                        <div>{errors.description}</div>
                      ) : null}
                    </div>
                    <div>
                      <label>Promotion Code:</label>
                      <Field name="promotionCode" type="text" />
                    </div>
                    <button type="submit" className="btn btn-success btn-block" disabled={orders.length === 0}>
                      Buy
                    </button>
                  </Form>
                )}
              </Formik>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Cart;
