import app from '../../api/baseApi';

const getOrderList = async function () {
	try {
		const response = await app.get('/ordertest');
		return response.data;
	} catch (error) {
		throw error;
	}
};

const createOrder = async function (data) {
	try {
		const response = await app.post('/ordertest', data); // Truyền trực tiếp `data`
		return response.data;
	} catch (error) {
		throw error;
	}
};

const updateOrder = async function (id, data) {
	try {
		const response = await app.put(`/ordertest/${id}`, data);
		return response.data;
	} catch (error) {
		throw error;
	}
};

const cancelOrder = async function (id) { // Không cần truyền `data`
	try {
		const response = await app.delete(`/ordertest/${id}`); // Không truyền `data`
		return response.data;
	} catch (error) {
		throw error;
	}
};

const respondToOrder = async function (id, data) {
	try {
		const response = await app.post(`/ordertest/${id}/response`, data);
		return response.data;
	} catch (error) {
		throw error;
	}
};

const confirmPayment = async function (id, data) {
	try {
		const response = await app.put(`/orders/payment/${id}`, data);
		return response.data;
	} catch (error) {
		throw error;
	}
};

const shipOrder = async function (id, data) {
	try {
		const response = await app.put(`/orders/shipping/${id}`, data);
		return response.data;
	} catch (error) {
		throw error;
	}
};

// Export các hàm sử dụng
const kitchenServices = {
	getOrderList,
	createOrder,
	updateOrder,
	cancelOrder,
	respondToOrder,
	confirmPayment,
	shipOrder,
};

export default kitchenServices;
